import './App.css';
import React from 'react';
// axiosで通信を行う
import axios from 'axios';



class NameForm extends React.Component<any, any> {
  // constructorはインスタンス作成時に実行
  constructor(props: any) {
    // propsを呼び出す
    super(props);
    this.state = {value: ''};

    // .bind(this)で意図したものを指定してしまうのを防ぐ
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }


  handleChange(event: any) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event: any) {
    alert('A name was submitted: ' + this.state.value);

    // htmlがもともと持っているformの送信をなくす
    event.preventDefault();
  }

  render() {
    return (
      <>
      {/* 送信したときにhandleSubmit発火 */}
      <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
      <h1>{this.state.value}</h1>
      </>
    );
  }
}

// 配列を描画
function ArrayContents(){
  const members = ["Araki", "Ibata", "Fukutome", "Woods", "Alex", "Tatsunami"];
  const list: any =[];
  members.forEach(member => list.push(member)+list.push(<br></br>));
  return <h1>{list}</h1>
}









// API通信Getのやり方（axiosを使用）
function App() {
  // apiの情報をstate管理
  const [statusName, setName] = React.useState('loading');
  const [statusWeather, setWeather] = React.useState('loading');

  // React.useEffect(() => { ... }, []);を入れることでaxiosの繰り返し処理を止めることができる
  React.useEffect(() => {

    // axios.get(URL)でapiからgetしてjsonを取得
    axios.get('https://api.openweathermap.org/data/2.5/weather?q=Tokyo,jp&appid=ab772c4cd38076eff2acf4701e560be6')
    // thenで成功した場合の処理をかける
    .then(response => {
      console.log(response);
      console.log(response.data.weather.[0].main);
      setName(response.data.name);
      setWeather(response.data.weather.[0].main);

      // catchでエラー時の挙動を定義する
    }).catch(err => {
      console.log('err:', err);
    });


  }, []);



    // onClickでclickedが発火
    const clicked = () =>{
      alert('ok');
    }


  return (
    <>
    <div className="App">
      <div className="">
        <NameForm />
        <ArrayContents />
        <p>下のボタンをクリックしてください。</p>
        <button onClick={()=>clicked()}>Click</button>
        <h1>City:{statusName}</h1>
        <h1>Weather:{statusWeather}</h1>
      </div>
    </div>
    </>
  );
}












