import './App.css';
import React from 'react';
// axiosで通信を行う
import axios from 'axios';

function ApiBtn(props){

  const clicked = () =>{

  }

  // axios.get(URL)でapiからgetしてjsonを取得
  axios.get('https://yesno.wtf/api')
  // thenで成功した場合の処理をかける
  .then(response => {
    console.log('status:', response.status); // 200
    console.log('body:', response.data);     // response body. 
    props.setStatus(response.status);    
    props.setBody(Object.values(response.data.image));    

    // catchでエラー時の挙動を定義する
  }).catch(err => {
    console.log('err:', err);
  });
  return <button>Click</button>
}

// API通信Getのやり方（axiosを使用）
function App() {
  // apiの情報をstate管理
  const [statusTxt, setStatus] = React.useState('loading');
  const [statusBody, setBody] = React.useState('loading');

  return (
    <>
    <ApiBtn />
    <h1>{statusTxt}</h1>
    <h1>{statusBody}</h1>
    <img src={statusBody}></img>
    </>
  );
}












